package com.javaex.basic.types;

public class charEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		char ch1 = 'R';
		char ch2 = '글';
		System.out.println(ch1);
		System.out.println(ch2);
		
		System.out.println(ch1 + ch2);
		
		String str = "A한"; // "는 문자열
		System.out.println(str);
		
		
	}

}
